import React, { Component } from "react";
import './Login.css';
import {Button, Container, Form} from "react-bootstrap";
import {useState, useEffect} from 'react';
import { BrowserRouter as Router, Route, Routes, NavLink, Link, } from "react-router-dom";
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";
import Main from "./Main";

function Login() {
  const [doctors, setDoctors] = useState([]); // patients is a state variable, setPatients is a func which updates it
  const [filteredData, setFilteredData] = useState(doctors);

  useEffect(() => {
    axios
    .get('http://127.0.0.1:8000/api/doctors/')

    .then(response => {
    console.log(response.data)
    setDoctors(response.data);
    setFilteredData(response.data);
    })
    .catch(error => {
    console.log('Error getting fake data: ' + error);
    })
    }, []);


  const SearchUsername = (e) =>{
    let value = e.target.value;
    let userfound = [];
    console.log(value);
    // result = patients.filter((data) => {
    //   return data.pesel.search(value) != -1;
    // });

    userfound = doctors.filter((data) => {
      return data.username.search(value) === "JMatejko";
    });


    setFilteredData(userfound);
    console.log(userfound);
    console.log(filteredData);
  }

  // const userfound = doctors.find(element => {
  //   return element.username === "JMatejko";
  // });



  // if (doctorFound) element = element=<a href={"http://localhost:3000/Main"}>Zaloguj</a>;
  // else element=<a href={"http://localhost:3000/404"}>Zaloguj</a>;

    return (
        <div>
          <Container id="main-container" className="d-grid h-100">
          <Form id="sign-in-form" className="text-center p-3 w-100">
            <h1 className="mb-3 fs-3 fw-normal">Body Temperature Cards Platform</h1>
            <Form.Group controlId="sign-in-email-address">
              <Form.Control type="username" size="lg" placeholder="UserName" onChange={(e) => SearchUsername(e)} className="position-relative" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="sign-in-password">
              <Form.Control type="password" size="lg" placeholder="Password" className="position-relative" />
            </Form.Group>
            <Form.Group className="d-flex justify-content-center mb-4" controlId="remember-me">
            </Form.Group>
            <div className="d-grid">
            <div>
              {/* {userfound && <h2>{userfound.username}</h2>} */}
            </div>
            {/* <a href="http://localhost:3000/Main" class="btn btn-primary">Sign In</a> */}
              {/* {element} */}
            </div>
            <p className="mt-5 text-muted">&copy; 2021-2022</p>
          </Form>
        </Container>
        </div>
    );
}

export default Login;



