import React, { Component } from "react";
import {Button, Container, Form} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

class AllPatients extends Component {
    constructor(props) {
        super(props);
        this.state = {
          activeItem: {
            id:null, 
              name:'',
              surname:'',
              birth_date:'',
              pesel:'',
              creation_date:'',
              gender:'',
              
          },
          PatientList: []
          };
          this.handleStatusChange = this.handleStatusChange.bind(this);
      }
        // async enables asynchronous operations, it returns a resolve value promise from our function
        async componentDidMount() {
          try {
            const res = await fetch('http://localhost:8000/api/patients');
            const PatientList = await res.json();
            this.setState({
                PatientList
            });
          } catch (e) {
            console.log(e);
        }
        }
        renderItems = () => {
          const newItems = this.state.PatientList();  
          return newItems.map(item => (
            <li 
              key={item.id}
              className="list-group-item d-flex justify-content-between align-items-center"
            >
              <span 
                className={`title mr-2 `}
                title={item.name}
                >
                  {item.title}
                </span>
            </li>
          ));
        };


    render(){
        return(
            <div>
                {/* <header>
                    <h1>All Patients</h1>
                    <p>Click on a selected Patient to show the studies.</p>
                </header> */}
                <Container id="main-container" className="d-grid h-100">
                    <main className="content">
                        <div className="row">
                        <div className="col-md-6 col-sm-10 mx-auto p-0">
                            <div className="card p-3">
                            <ul className="list-group list-group-flush">
                                {this.renderItems()}
                            </ul>
                            </div>
                        </div>
                        </div>
                    </main>
               
                </Container>
            </div>
        )
    }
}
